  function getValueOfOption(option) {
    switch (option) {
      case "1 = Independent":
        return 1;
      case "2 = Independent with mild difficulties":
        return 2;
      case "3 = Independent with severe difficulties":
        return 3;
      case "4 = Complete dependent/Unable to do":
        return 4;
      default:
        return 0;
    }
  }

  const concepts = [
    'OTS, Obtaining and using toilet supplies',
    'OTS, Managing clothes before and after toileting',
    'OTS, Washing (including hair)',
    'OTS, Drying',
    'OTS, Turning taps',
    'OTS, Managing hair',
    'OTS, Use nail clipper',
    'OTS, Cleaning teeth',
    'OTS, Perfuming',
    'OTS, Washing hands',
    'OTS, Shaving',
    'OTS, Fastening buttons',
    'OTS, Fastening zippers',
    'OTS, Put on and remove pants',
    'OTS, Put on and remove shirts',
    'OTS, Fastening belts',
    'OTS, Put on shoe and tie laces',
    'OTS, Tie head scarf/cap',
    'OTS, Holding cutlery',
    'OTS, Kitchen tasks',
    'OTS, Bread activity',
    'OTS, Helping with household jobs',
    'OTS, Using work appliances',
    'OTS, Using door handle an electric tool',
    'OTS, Using writing tools',
    'OTS, Using keyboard and mouse',
    'OTS, Driving',
    'OTS, Play/leisure',
    'OTS, Shopping and carry grocery bags'
  ]

  const arrayOfValues = concepts.map(element => form.get(element).getValue())
  let result = undefined;

  if (arrayOfValues.some(value => value)) {
    result = arrayOfValues.reduce((sum, value) => sum + getValueOfOption(value), 0);
  }

