changeSet.sql:
set @concept_id = 0;
set @concept_short_id = 0;
set @concept_full_id = 0;
call add_concept(@concept_id,@concept_short_id,@concept_full_id,"µg","µg","N/A","Misc",false);
call add_concept(@concept_id,@concept_short_id,@concept_full_id,"application(s)","application(s)","N/A","Misc",false);
call add_concept(@concept_id,@concept_short_id,@concept_full_id,"dose","dose","N/A","Misc",false);
call add_concept(@concept_id,@concept_short_id,@concept_full_id,"gram","gram","N/A","Misc",false);
call add_concept(@concept_id,@concept_short_id,@concept_full_id,"mmol","mmol","N/A","Misc",false);
call add_concept(@concept_id,@concept_short_id,@concept_full_id,"puff(s)","puff(s)","N/A","Misc",false);

SELECT concept_id INTO @concept_id1 FROM concept_name WHERE name = "capsule(s)" AND concept_name_type ="FULLY_SPECIFIED";
SELECT concept_id INTO @concept_id2 FROM concept_name WHERE name = "tablet(s)" AND concept_name_type ="FULLY_SPECIFIED";
SELECT concept_id INTO @parent_concept_id FROM concept_name WHERE name = "Dosing Units" AND concept_name_type ="FULLY_SPECIFIED";
delete from concept_set where concept_id=@concept_id1 and concept_set= @parent_concept_id;
delete from concept_set where concept_id=@concept_id2 and concept_set= @parent_concept_id;

preConditions.sqlCheck:
SELECT count(*) from concept_set where concept_id in (select concept_id from concept_name where name in ("g", "application(s)", "capsule(s)", "dose", "gram", "IU", "mg", "ml", "mmol", "puff(s)", "tablet(s)")  
AND concept_name_type ="FULLY_SPECIFIED") 
AND concept_set =(SELECT concept_id  from concept_name where name= 'Dosing Units' AND concept_name_type="FULLY_SPECIFIED");

